import { capitalize } from 'vue';

function withInstall(options) {
    options.install = (app) => {
        const { name } = options;
        // 短横线命名
        app.component(name, options);
        // 大驼峰命名
        let _nameArray = name.split('-').slice(1);
        if (_nameArray) {
            _nameArray = _nameArray.map((item) => capitalize(item));
            app.component(_nameArray.join(''), options);
        }
    };
    return options;
}

export { withInstall };
